import React, { useState } from "react";
import "../css/landing.css";
import { motion } from "framer-motion";
import { Link, useNavigate, useLocation } from "react-router-dom";
import "../css/login.css";

function Form(formChoice) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [organization, setOrganization] = useState("");

  const navigate = useNavigate();
  const location = useLocation();
  console.log(formChoice);
  if (formChoice == "default") {
    return <div></div>;
  }
  if (formChoice == "formulaAndFood") {
    function updateForm(e) {
      if (e.target.id === "firstName") {
        setFirstName(e.target.value);
      }
      if (e.target.id === "lastName") {
        setLastName(e.target.value);
      }
      if (e.target.id === "organization") {
        setOrganization(e.target.value);
      }
    }

    const handleSubmit = async (e) => {
      e.preventDefault(); /*
    fetch("http://127.0.0.1:4000/users/me", {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        Authorization: location.state.token,
      },
      body: JSON.stringify({
        firstName,
        lastName,
        organization,
      }),
    })
      .then((res) => console.log(res))
      .then(() => {
        navigate(
          "../signup/primary-use",
          {
            state: {
              email: location.state.email,
              firstName,
              lastName,
              token: location.state.token,
            },
          },
          { replace: true }
        );
      })
      .catch((error) => {});*/
    };
    return (
      <div>
        <form action="POST" onSubmit={handleSubmit}>
          <input
            type="text"
            id="firstName"
            value={firstName}
            onChange={(e) => updateForm(e)}
            placeholder="First name"
            className="defaultBtn"
          />
          <input
            type="text"
            id="lastName"
            value={lastName}
            onChange={(e) => updateForm(e)}
            placeholder="Last name"
            className="defaultBtn"
          />
          <input
            type="text"
            id="organization"
            value={organization}
            onChange={(e) => updateForm(e)}
            placeholder="Organization name (optional)"
            className="passwordLogin loginBtn"
          />
          <input
            type="submit"
            className="submitLogin loginBtn"
            value="Continue"
          />
        </form>
        <div className="loginToSignup">
          <p className="dontLogin">By clicking "Continue", you agree to our</p>
          <Link to="../terms-of-use" className="loginLinkToSignup">
            terms
          </Link>
        </div>
        <p className="confirmAge">and confirm you're 18 years or older.</p>
      </div>
    );
  }
}

export default Form;
