import React from "react";
import "../css/landing.css";
import hero from "../images/hero.jpg";
import arrow from "../images/rightArrow.png";
import { FaArrowRight } from "react-icons/fa";
import { motion } from "framer-motion";

function Landing() {
  return (
    <div>
      <img src={hero} id="heroImg" />
      <div id="heroText">
        <motion.h1
          className="everythingText"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          Everything you need to<br></br>solve anything you want.
        </motion.h1>
        <motion.h1
          className="withKeyText"
          initial={{ opacity: 0, y: 25 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          With key AI Tools, magic calibrations, and more, ChemE.AI<br></br>is
          your next-generation problem solving suite.
        </motion.h1>
        <motion.a
          href="/signin"
          className="tryNow"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <button type="button" className="btn tryNowHighlight">
            TRY CHEME.AI FOR FREE
          </button>
          <FaArrowRight className="rightArrow" transform="grow-20" beat />
        </motion.a>
      </div>
    </div>
  );
}

export default Landing;
