import React, { useState } from "react";
import "../../css/login.css";
import logo from "../../images/logoBlack.png";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import PhoneInput from "react-phone-input-2";

function VerifyPhone() {
  const [phone, setPhone] = useState("");

  const navigate = useNavigate();
  const location = useLocation();

  function updateForm(e) {
    if (e.target.id === "phone") {
      setFirstName(e.target.value);
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    navigate(
      "../signup/note-on-credits",
      { state: { form: location.state.form } },
      { replace: true }
    );
  };

  return (
    <motion.div
      className="loginPage"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <img src={logo} className="loginLogo" />
      <h2 className="welcomeLogin">Verify your phone number</h2>
      <form action="POST" onSubmit={handleSubmit}>
        <label for="passwordLogin">Phone number</label>
        <input
          type="tel"
          id="phone"
          onChange={(e) => updateForm({ password: e.target.value })}
          placeholder="+1(999)999-9999"
          className="passwordLogin loginBtn"
        />
        <input
          type="submit"
          className="submitLogin loginBtn"
          value="Send code"
        />
      </form>
    </motion.div>
  );
}

export default VerifyPhone;
