import React, { useState } from "react";
import Header from "../../components/header";
import "../../css/login.css";
import logo from "../../images/logoBlack.png";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

function AboutYou() {
  const [form, setForm] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
    primaryUse: "",
    phoneNumber: "",
    organization: "",
  });

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    navigate("about-you", { state: form });
    /*
    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    }).catch((error) => {
      window.alert(error);
      return;
    });

    setForm({ name: "", position: "", level: "" });
    navigate("/");
    */
  };

  return (
    <motion.div
      className="loginPage"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <img src={logo} className="loginLogo" />
      <h2 className="welcomeLogin">Welcome Back</h2>
      <form action="POST" onSubmit={handleSubmit}>
        <input
          type="email"
          value={form.email}
          onChange={(e) => updateForm({ email: e.target.value })}
          placeholder="Email address"
          className="emailLogin loginBtn"
        />
        <input
          type="password"
          value={form.password}
          onChange={(e) => updateForm({ password: e.target.value })}
          placeholder="Password"
          className="passwordLogin loginBtn"
        />
        <Link
          to="forgot-password"
          state={{ email: form.email }}
          className="forgotPassword"
        >
          Forgot password?
        </Link>
        <input
          type="submit"
          className="submitLogin loginBtn"
          value="Continue"
        />
      </form>
      <div className="loginToSignup">
        <p className="dontLogin">Don't have an account?</p>
        <Link to="../signup" className="loginLinkToSignup">
          Sign up
        </Link>
      </div>
    </motion.div>
  );
}

export default AboutYou;
