import React, { useState } from "react";
import "../../css/login.css";
import logo from "../../images/logoBlack.png";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

function NoteOnCredits() {
  const [form, setForm] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
    primaryUse: "",
    phoneNumber: "",
    organization: "",
  });

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    /*
    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    }).catch((error) => {
      window.alert(error);
      return;
    });

    setForm({ name: "", position: "", level: "" });
    navigate("/");
    */
  };

  return (
    <motion.div
      className="loginPage"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <img src={logo} className="loginLogo" />
      <h2 className="welcomeLogin">A note on credits</h2>
      <p className="pleaseNoteLogin">
        Because this phone number is associated with an existing account, you
        will not receive additional free trial credits. Please upgrade to a paid
        plan to start using the API. If you need further assistance, please
        contact us through our help center at help.openai.com
      </p>
      <form action="POST" onSubmit={handleSubmit}>
        <input
          type="submit"
          className="submitLogin loginBtn"
          value="Continue"
        />
      </form>
    </motion.div>
  );
}

export default NoteOnCredits;
