import React, { useState } from "react";
import "../../css/login.css";
import logo from "../../images/logoBlack.png";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { motion } from "framer-motion";

function PrimaryUse() {
  const [form, setForm] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
    primaryUse: "",
    phoneNumber: "",
    organization: "",
  });

  const navigate = useNavigate();
  const location = useLocation();

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    navigate(
      "../signup/verify-phone",
      { state: { form: location.state.form } },
      { replace: true }
    );
    /*
    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    }).catch((error) => {
      window.alert(error);
      return;
    });

    setForm({ name: "", position: "", level: "" });
    navigate("/");
    */
  };

  return (
    <motion.div
      className="loginPage"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <img src={logo} className="loginLogo" />
      <h2 className="welcomeLogin">How will you primarily use ChemE.AI?</h2>
      <button type="button" value="building" className="signupUse">
        I'm a chemical engineering student
      </button>
      <button type="button" value="exploring" className="signupUse">
        I'm exploring personal use
      </button>
      <button type="button" value="research" className="signupUse">
        I'm studying another engineering
      </button>
      <button type="button" value="content" className="signupUse">
        I'm a chemical engineer from industry
      </button>
    </motion.div>
  );
}

export default PrimaryUse;
