import React, { useEffect, useState } from "react";
import logo from "../images/whiteLogo.png";
import "../css/header.css";

function Header() {
  const [headerClassName, setHeader] = useState("header");
  const handleScroll = () => {
    window.addEventListener("scroll", (event) => {
      let headerClassName = window.scrollY > 0 ? "headerHover" : "header";
    });
  };
  return (
    <div className={headerClassName} onScroll={handleScroll}>
      <a href="/">
        <img src={logo} className="headerLogo" />
      </a>
      <a href="/features" className="featuresWrapper">
        <button type="button" className="features btn">
          Features
        </button>
      </a>
      <a href="/signin" className="signIn">
        <button type="button" className="btn signInHighlight">
          Sign In
        </button>
      </a>
    </div>
  );
}

export default Header;
