import React, { useEffect, useState } from "react";
import logo from "../images/whiteLogo.png";
import "../css/header.css";

function Header() {
  const [signInClassName, setSignInClassName] = useState("sigIn");
  const handleScroll = () => {
    const scrollPosition = window.scrollY; // => scroll position
  };
  useEffect(() => {
    handleScroll();
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="header">
      <a href="/">
        <img src={logo} className="headerLogo" />
      </a>
      <a href="/features" className="featuresWrapper">
        <button type="button" className="features btn">
          Features
        </button>
      </a>
      <a href="/signin" className={signInClassName}>
        <button type="button" className="btn signInHighlight">
          Sign In
        </button>
      </a>
    </div>
  );
}

export default Header;
